let countdownTimers=null;function loadTimerElements(){countdownTimers=document.querySelectorAll(".live-timer-countdown")}function statrtCountdownTimerLoop(){const lsKey=`countdown_rst`;setInterval((function(){countdownTimers.forEach((el=>{const endTs=el.dataset.endTime*1e3;const nowTs=Date.now();const timeLeft=endTs-nowTs;const latestNextRoi=parseInt(localStorage.getItem(lsKey));if(timeLeft>0){const seconds=Math.floor(timeLeft/1e3%60);const minutes=Math.floor(timeLeft/(1e3*60)%60);const hours=Math.floor(timeLeft/(1e3*60*60)%24);el.textContent=`${hours}h ${minutes}m ${seconds}s`}else if(!latestNextRoi){localStorage.setItem(lsKey,nowTs+1e4)}}));const latestNextRoi=parseInt(localStorage.getItem(lsKey));const nowTs=Date.now();if(latestNextRoi&&nowTs>=latestNextRoi){localStorage.removeItem(lsKey);}}),250)}document.addEventListener("DOMContentLoaded",(event=>{loadTimerElements();statrtCountdownTimerLoop()}));