const createNotification=(header,body)=>`\n    <div class="notification_container notification_slidein">\n        <button onclick="closeNotification(this)" class="notification_close"><i class="fa-regular fa-circle-xmark fa-lg"></i></button>\n        <div class="notification_header">\n            <h4>${header}</h4>\n        </div>\n        <div class="notification_body">\n            ${body}\n        </div>\n    </div>`;const notificationContainer=document.querySelector(".notifications-widget");const createElement=elementString=>{const template=document.createElement("div");template.innerHTML=elementString.trim();return template.firstElementChild};function closeNotification(element){element.parentElement.remove()}function showTransactionNotification(type,email,symbol,amountCrypto,amountFiat){const el=createElement(createNotification(type,`<div class="mb-1">\n            <span>${email}</span>\n        </div>\n        <div class="flex gap-1">\n            <img src="/images/cryptoicons/SVG/${symbol.toLowerCase()}.svg" width="18" height="18" alt="">\n            <div class="flex items-end flex-col">\n                <span style="font-size: 0.8em;">${amountCrypto} ${symbol}</span>\n                <span style="">$${amountFiat}</span>\n            \n            </div>\n        </div>`));notificationContainer.appendChild(el);setTimeout((function(){el.classList.remove("notification_slidein");el.classList.add("notification_slideout");setTimeout((()=>{el.remove()}),2e3)}),8e3)}addSseListener("transaction",(function(e){const{type:type,email:email,symbol:symbol,amountCrypto:amountCrypto,amountFiat:amountFiat}=JSON.parse(e.data);showTransactionNotification(type,email,symbol,amountCrypto,amountFiat)}));